mapper.update:
update tblsite set
	SiteID		     = #{SiteID}
	, SiteName       = #{SiteName}
	, SiteBNo        = #{SiteBNo}
	, Pname 	     = #{Pname}
	, UpTae 	     = #{UpTae}
	, JongMok	     = #{JongMok}
	, ZipCode        = #{ZipCode}
	, Address        = #{Address}
	, SubAddress     = #{SubAddress}
	, Phone1	     = #{Phone1}
	, Phone2 	 	 = #{Phone2}
	, Fax 		 	 = #{Fax}
	, Email			 = #{Email}
	, DamDangJa  	 = #{DamDangJa}
	, DamDangJaPhone = #{DamDangJaPhone}
	, Note			 = #{Note}
	, UpdDate  = sysdate()
where
	IsDelete = 'N'
and SiteCode = #{SiteCode}

mapper.select:
select * from tblPgmGroup 
where 
	IsDelete = 'N'
and SiteCode = {SiteCode}
and PGMGroupID = #{PGMGroupID}

select * from tblpgm 
where 
	IsDelete = 'N'
and SiteCode = #{SiteCode}
and pkid = #{pkid}

select * from tblcode
where 
	SiteCode  = #{SiteCode}
and CodeGroupID = #{CodeGroupID}
and IsDelete = 'N'
order by sortOrder asc

select CodeName from tblcode
where 
	SiteCode  = #{SiteCode}
and CodeGroupID = #{CodeGroupID}
and IsDelete = 'N'
order by sortOrder asc

mapper.update:
UPDATE tblcode
SET CodeValue = CASE WHEN CodeName = #{CodeName} THEN 1 ELSE 0 END
WHERE SiteCode = #{SiteCode} AND CodeGroupID = #{CodeGroupID}

mapper.select:
select CodeName from tblcode
where 
	SiteCode  = #{SiteCode}
and CodeGroupID = #{CodeGroupID}
and IsDelete = 'N'
and CodeValue = '1'
