mapper.select:
select a.PLockerID,c.PLockerGroupName,c.PLockerLocation,a.PLockerNO,a.State,a.repairID, b.MemberID,b.Name,a.regmonth,a.SortOrder from tblplocker a left join tblmember b on a.MemberID = b.MemberID left join tblplockergroup c on a.PLockerGroupID = c.PLockerGroupID where a.SiteCode = #{SiteCode}
and a.IsDelete = 'N'
and a.IsDelete = 'Y'
AND c.PLockerGroupID = #{PLockerGroupID}
AND a.State = #{State}

select *
from
	tblplocker
where
    SiteCode = #{SiteCode}
and PLockerID = #{PLockerID}

mapper.update:
update tblplocker set
	PLockerGroupID = #{PLockerGroupID}
	,PLockerNO = #{PLockerNO}
	,SortOrder = #{SortOrder}
	,State = #{State}
	,RepairID = #{RepairID}
	,lSaleNo = #{lSaleNo}
	,FromDate = #{FromDate}
	,ToDate = #{ToDate}
	,RegMonth = #{RegMonth}
	,MemberID = #{MemberID}
	,IsDelete = #{IsDelete}
	,AddDate = #{AddDate}
	,AddUserPKID = #{AddUserPKID}
	,UpdDate = now()
	,UpdUserPKID = #{UpdUserPKID}
	,updchk = 'up_update'
where
	PLockerID = #{PLockerID}
