mapper.select:
select 
	PKID , GroupName , SiteCode , SortOrder , GroupJungWon , ParkingTimeWeek , ParkingTimeWeekend
from fs_dsgroup where SiteCode = #{SiteCode}
and IsDelete = 'N'
and pkid = #{pkid}

mapper.update:
update fs_dsgroup set
	GroupName = #{GroupName}
	, SortOrder = #{SortOrder}
	, GroupJungWon = #{GroupJungWon}
	, ParkingTimeWeek = #{ParkingTimeWeek}
	, ParkingTimeWeekend = #{ParkingTimeWeekend}
	, UpdUserPKID = ${UpdUserPKID}
	, UpdDate = sysdate()
where 
	SiteCode = #{SiteCode}
and IsDelete = 'N'
and pkid = #{pkid}

update fs_dsgroup set
	IsDelete = 'Y'
	, UpdUserPKID = #{UpdUserPKID}
	, UpdDate  = sysdate()
where 
	SiteCode = #{SiteCode}
and	pkid = #{pkid}

update fs_dsitem set
	itemName = #{itemName}
	, ShortName = #{ShortName}
	, GroupCode = #{GroupCode}
	, FromTime = #{FromTime}
	, ToTime = #{ToTime}
	, AdultGBN = #{AdultGBN}
	, Gender = #{Gender}
	, Price = #{Price}
	, VAT = #{vat}
	, NVAT = #{Nvat}
	, Jungwon = #{Jungwon}
	, Upjang = #{UpJang}
	, KioskYN = #{KioskYN}
	, DCType = #{dcType}
	, Mon = #{Mon}
	, Tue = #{Tue}
	, Wed = #{Wed}
	, Thu = #{Thu}
	, Fri = #{Fri}
	, Sat = #{Sat}
	, Sun = #{Sun}
	, SortOrder = #{SortOrder}
	, UpdUserPKID = #{UpdUserPKID}
	, UpdDate = sysdate()
where 
	SiteCode = #{SiteCode}
and IsDelete = 'N'
and pkid = #{pkid}

update fs_dsitem set
	IsDelete = 'Y'
	, UpdUserPKID = #{UpdUserPKID}
	, UpdDate  = sysdate()
where 
	SiteCode = #{SiteCode}
and	pkid = #{pkid}

mapper.select:

	select ifNull(sum(ifNull(Amount,0)),0) as TotalAmount 
	from sl_orders_details a
	left join sl_orders b on a.sitecode = b.sitecode and a.orderID = b.OrderID
	where a.sitecode = #{SiteCode}
	and a.itemPKID = #{ItemPKID}
	and b.isDelete = 'N'
	and b.saledate = #{SaleDate}


	select Jungwon, IfNull(GroupJungwon,0) as GroupJungwon
	from FS_DSItem a
	left join FS_DSGroup b on a.sitecode = b.sitecode and a.GroupCode = b.PKID
	where a.Sitecode = #{SiteCode}
	and a.isDelete = 'N'
	and a.PKID = #{ItemPKID}
